import "./Chessboard.css";
import Tile from "./Tile";

//images
import pawn_w from "../Pieces/pawn_w.png";
import pawn_b from "../Pieces/pawn_b.png";
import rook_w from "../Pieces/rook_w.png";
import rook_b from "../Pieces/rook_b.png";
import bishop_w from "../Pieces/bishop_w.png";
import bishop_b from "../Pieces/bishop_b.png";
import knight_w from "../Pieces/knight_w.png";
import knight_b from "../Pieces/knight_b.png";
import queen_w from "../Pieces/queen_w.png";
import queen_b from "../Pieces/queen_b.png";
import king_w from "../Pieces/king_w.png";
import king_b from "../Pieces/king_b.png";

let x = ["a", "b", "c", "d", "e", "f", "g", "h"];
let y = ["1", "2", "3", "4", "5", "6", "7", "8"];
const Chessboard = () => {
  let board = [];
  let i, j;

  for (i = 0; i <= 7; i++) {
    for (j = 0; j <= 7; j++) {
      const num = i + j + 2;
      console.log(x[j], y[i]);

      //white Pieces
      if (y[i] == "2")
        board.push(<Tile image={pawn_w} num={num} x={x[j]} y={y[i]} />);
      else if ((x[j] == "a" && y[i] == "1") || (x[j] == "h" && y[i] == "1"))
        board.push(<Tile image={rook_w} num={num} x={x[j]} y={y[i]} />);
      else if ((x[j] == "b" && y[i] == "1") || (x[j] == "g" && y[i] == "1"))
        board.push(<Tile image={knight_w} num={num} x={x[j]} y={y[i]} />);
      else if ((x[j] == "c" && y[i] == "1") || (x[j] == "f" && y[i] == "1"))
        board.push(<Tile image={bishop_w} num={num} x={x[j]} y={y[i]} />);
      else if (x[j] == "d" && y[i] == "1")
        board.push(<Tile image={queen_w} num={num} x={x[j]} y={y[i]} />);
      else if (x[j] == "e" && y[i] == "1")
        board.push(<Tile image={king_w} num={num} x={x[j]} y={y[i]} />);
      else if ((x[j] == "a" && y[i] == "1") || (x[j] == "h" && y[i] == "1"))
        board.push(<Tile image={rook_w} num={num} x={x[j]} y={y[i]} />);
      //Black Pieces
      else if (y[i] == "7")
        board.push(<Tile image={pawn_b} num={num} x={x[j]} y={y[i]} />);
      else if ((x[j] == "a" && y[i] == "8") || (x[j] == "h" && y[i] == "8"))
        board.push(<Tile image={rook_b} num={num} x={x[j]} y={y[i]} />);
      else if ((x[j] == "b" && y[i] == "8") || (x[j] == "g" && y[i] == "8"))
        board.push(<Tile image={knight_b} num={num} x={x[j]} y={y[i]} />);
      else if ((x[j] == "c" && y[i] == "8") || (x[j] == "f" && y[i] == "8"))
        board.push(<Tile image={bishop_b} num={num} x={x[j]} y={y[i]} />);
      else if (x[j] == "d" && y[i] == "8")
        board.push(<Tile image={queen_b} num={num} x={x[j]} y={y[i]} />);
      else if (x[j] == "e" && y[i] == "8")
        board.push(<Tile image={king_b} num={num} x={x[j]} y={y[i]} />);
      else if ((x[j] == "a" && y[i] == "1") || (x[j] == "h" && y[i] == "1"))
        board.push(<Tile image={rook_w} num={num} x={x[j]} y={y[i]} />);
      else board.push(<Tile num={num} x={x[j]} y={y[i]} />);
    }
  }
  return <div id="chessboard">{board}</div>;
};

export default Chessboard;
//{x[j]}{y[i]}
