import "./Tile.css";
// import pawn_w from "../../public/Pieces/pawn_w.png"

interface props {
  image?: string;
  num: number;
  x: string;
  y: string;
}

const Tile = ({ image, num, x, y }: props) => {
  //{X}{y}
  if (num % 2 == 0)
    return (
      <div className="tile black-tile">
        <img src={image} />
      </div>
    );
  else
    return (
      <div className="tile white-tile">
        <img src={image} />
      </div>
    );
};

export default Tile;
